import 'dotenv/config';

import 'reflect-metadata';

import express from 'express';
import cors from 'cors';
import config from 'config';
import log from './logger';

import routes from './routes';


/**
 * Variables
 */
const host: string = config.get('host') as string;
const port: number = config.get('port') as number;

const app = express();


/**
 * Middlewares
 */
app.use(cors());
app.use(express.json());


/**
 * Routes
 */
 routes(app);


/**
 * Server activation
 */
app.listen(port, host, () => {
    log.info(`Server listening at http://${host}:${port}`);
});

export default app;

export default {
    host: process.env.HOST || 'localhost',
    port: parseInt(<string>process.env.PORT, 10) || 3001,
    simpleArray: process.env.SIMPLE_ARRAY?.split(',') || ['post', 'get', 'put', 'delete'],
    countriesApiUri: 'https://api.jsonbin.io/b/5f69afbe65b18913fc510ce8'
}

import logger from 'pino';
import dayjs from 'dayjs';

const log = logger({
    prettyPrint: true,
    base: {
        pid: false
    },
    timestamp: () => `,"time":"${dayjs().format()}"`
});

export default log;

import { Express } from 'express';
import countriesRoutes from './countries.routes';
import reverseRoutes from './reverse.routes';
import apiOperationsRoutes from './api-operations.routes';

const routes = (app: Express) => {

    app.use('/countries', countriesRoutes);

    app.use('/reverse', reverseRoutes);

    app.use('/append', apiOperationsRoutes);

};

export default routes;

import { Router } from "express";
import Container from "typedi";
import CountryController from "../controllers/countries.controller";

class Routes {

    private router: Router = Router();

    private controller: CountryController = Container.get(CountryController);

    constructor() {
        this.setRoutes();
    }

    public setRoutes(): void {
        this.router.get('/', (req, res) => this.controller.getCountries(req, res));
    }

    public getRoutes(): Router {
        return this.router;
    }
}

const routes = new Routes()
export default routes.getRoutes();

import { Request, Response } from "express";
import { Service } from "typedi";
import CountryService from '../services/countries.service';
import { CountriesQueryParams } from '../models/countries-query-params.model';
import { OrderParam, OrderParamType } from "../models/order-param.enum";

import log from '../logger';

@Service()
export default class CountryController {

    constructor(
        private countryService: CountryService
    ) { }

    public async getCountries(req: Request, res: Response): Promise<Response> {

        try {
            const { filter, order } = req.query;

            if (
                (filter && typeof filter !== 'string') ||
                (order && !(order.toString() in OrderParam))
            ) return res.sendStatus(400)

            const queryParams: CountriesQueryParams = {
                filter: filter ? filter : undefined,
                order: order ? order as OrderParamType : undefined
            };

            const countries = await this.countryService.getCountries(queryParams);
            return res.status(200).json({data: countries});

        } catch (error) {
            log.error(error);
            return res.sendStatus(500);
        }
    }
}

import { Service } from 'typedi';
import CountriesDB from '../db/countries.db';
import { Country } from '../models/country.model';
import { OrderParamType } from '../models/order-param.enum';
import { CountriesQueryParams } from '../models/countries-query-params.model';
import log from '../logger';

@Service()
export default class CountryService {

    constructor(
        private countriesDB: CountriesDB
    ) { }

    public async getCountries(queryParams?: CountriesQueryParams): Promise<Country[]> {

        try {
            log.info('Fetching all countries from API');

            const countries = await this.countriesDB.getCountries();

            if (!queryParams) {
                return countries;
            }

            let countriesResult: Country[] = countries;

            const { filter, order } = queryParams;

            if (filter) {
                countriesResult = this.filterCountries(countries, filter);
            }

            if (order) {
                countriesResult = this.sortCountries(countriesResult, order);
            }

            return countriesResult;

        } catch (error) {
            log.error(error);
            return [];
        }
    }

    public filterCountries(countries: Country[], searchString: string): Country[] {

        // Makes the search string a regular expression with case insensitive
        const regex = new RegExp(searchString, 'i');

        return countries.filter(
            (country: Country) => (
                regex.test(country.country) ||
                regex.test(country.code)
            )
        );
    }

    public sortCountries(countries: Country[], order: OrderParamType): Country[] {
        return countries.sort(
            (a: Country, b: Country) => (
                (order === 'asc')
                    ? a.vat - b.vat
                    : b.vat - a.vat
            )
        );
    }
}

import axios, { AxiosResponse } from "axios";
import { Service } from "typedi";
import { Country } from '../models/country.model';
import config from "config";


@Service()
export default class CountriesDB {

  private apiUri: string;

  constructor () {
    this.apiUri = config.get('countriesApiUri') as string;
  }

  public getCountries(): Promise<Country[]> {
    return axios.get(this.apiUri)
      .then((res: AxiosResponse) => res.data);
  }
}

export enum OrderParam {
    'asc',
    'desc'
}

export type OrderParamType = keyof typeof OrderParam;

import { Router } from "express";
import Container from "typedi";
import ReverseController from "../controllers/reverse.controller";

class Routes {

    private router: Router = Router();

    private controller: ReverseController = Container.get(ReverseController);

    constructor() {
        this.setRoutes();
    }

    public setRoutes(): void {
        this.router.get('/:urlParam', (req, res) => this.controller.getReversedData(req, res));
    }

    public getRoutes(): Router {
        return this.router;
    }
}

const routes = new Routes()
export default routes.getRoutes();

import { Request, Response } from 'express';
import { Service } from 'typedi';
import ReverseService from '../services/reverse.service';

@Service()
export default class ReverseController {

    constructor (
        private reverseService: ReverseService
    ) {}

    public getReversedData(req: Request, res: Response): Response {

        const data: string = req.params.urlParam as string;

        const processedData: string = this.reverseService.getProcessedParam(data);

        return res.status(200).json({ data: processedData });
    }
}

import { Service } from "typedi";

@Service()
export default class ReverseService {

    constructor() { }

    public getProcessedParam(urlParam: string): string {
        const reversedLetters: string[] = this.reverseString(urlParam);
        const processedLetters: string[] = this.transformVowelsToUppercase(reversedLetters);
        return processedLetters.join('');
    }

    public reverseString(data: string): string[] {
        return data
            .split('')
            .reverse();
    }

    public transformVowelsToUppercase(letters: string[]): string[] {
        return letters.map((letter: string) => {
            if (this.isVowel(letter)) {
                return letter.toUpperCase();
            }

            return letter;
        })
    }

    /**
     * Caution!!!
     *
     * This logic return true if @param letter contains a vowel.
     * Therefore, the input param MUST be a single character to properly check if it's a vowel
     */
    public isVowel(letter: string): boolean {
        const vowelsRegex: RegExp = /[aeiou]/i;

        return letter.match(vowelsRegex)
            ? true
            : false;
    }
}

import { Router } from "express";
import Container from "typedi";
import ApiOperationController from "../controllers/api-operation.controller";

class Routes {

    private router: Router = Router();

    private controller: ApiOperationController = Container.get(ApiOperationController);

    constructor() {
        this.setRoutes();
    }

    public setRoutes(): void {
        this.router.put('/', (req, res) => this.controller.updateApiOperations(req, res));
    }

    public getRoutes(): Router {
        return this.router;
    }
}

const routes = new Routes()
export default routes.getRoutes();

import { Request, Response } from "express";
import { Service } from "typedi";
import { ApiOperationsQueryParams } from "../models/api-op-query-params.model";
import ApiOperationsService from '../services/api-operations.service';


@Service()
export default class ApiOperationController {

    constructor(
        private apiOperationsService: ApiOperationsService
    ) { }

    public updateApiOperations(req: Request, res: Response): Response {

        const { start: startQuery, end: endQuery } = req.query;

        if (
            (!startQuery && !endQuery) ||
            (startQuery && typeof startQuery !== 'string') ||
            (endQuery && typeof endQuery !== 'string')
        ) return res.sendStatus(400)

        const queryParams: ApiOperationsQueryParams = {
            start: startQuery ? startQuery : undefined,
            end: endQuery ? endQuery : undefined
        };

        const apiOperations: string[] = this.apiOperationsService.updateApiOperations(queryParams);

        return res.status(200).json({ data: apiOperations });
    }
}

import { Service } from "typedi";
import config from 'config';
import { ApiOperationsQueryParams } from "../models/api-op-query-params.model";

@Service()
export default class ApiOperationsService {

    private apiOperations: string[] = [];

    constructor() {
        const operationsDefault = config.get('simpleArray') as string[];
        this.apiOperations.push(...operationsDefault);
    }

    public updateApiOperations(queryParams: ApiOperationsQueryParams): string[] {

        const { start, end } = queryParams;

        if (start) {
            this.apiOperations.unshift(start);
        }

        if (end) {
            this.apiOperations.push(end);
        }

        return this.apiOperations;
    }
}
